"use strict";
// union type example
// A union type in TypeScript means that a variable can hold values of multiple specified types. You can define a variable that can be one of several types by using the | symbol.
let id;
id = 101;
id = "abc";
console.log(id);
let a = {
    name: "ALI",
    id: 101,
    position: "developer",
};
console.log(a);
let staff = {
    name: "Ali Sher",
    age: 25,
    employeeId: 123,
    position: "Front end Developer",
};
console.log(staff);
